import React, {useState, useEffect} from 'react'
import  './App.css'
import Typewriter from 'typewriter-effect';


const words = 'Hello World'

function App() {
    const [typedWord, setTypedWord] = useState('')
    useEffect(() => {
      const nextTypedWord = words.slice(0, typedWord.length + 1)

      if(nextTypedWord === typedWord) return

      const wordtyper = setTimeout(() => {
         setTypedWord(words.slice(0, typedWord.length + 1))
       }, 100);

       return () => clearTimeout(wordtyper)
    }, [typedWord])


  return (
    <div>
      <Typewriter
        options={{
        strings: ['Hello', 'World'],
        autoStart: true,
        loop: true,
        }}
      />
      <h1 className="typing">{typedWord}</h1>
    </div>
  )
}

export default App
